__author__ = 'student'import randomimport matplotlib.pyplot as pltimport numpy as npvalues = [3, 4, 1, 2, 5, 6, 7, 8, 9, 10]bucket_number=4def get_percentile(values, bucket_number):    percentiles=[]    for i in range(bucket_number):        if i==0:            p=0        else:            p=np.percentile(values, 100*i/bucket_number)        percentiles.append(p)    return percentilesprint(get_percentile(values, 4))percentile=[]value=input()percentiles=get_percentile(values, bucket_number)def get_percentile_number(value, percentiles):    i=0    while percentiles[i]<=value[0]:            i+=1    return iprint(get_percentile_number(value, percentiles))